import{_ as a,c as i,o as e,af as r}from"./chunks/framework.4VVWv4NI.js";const u=JSON.parse('{"title":"Начало игры на Antagon SMP","description":"","frontmatter":{},"headers":[],"relativePath":"guide/start.md","filePath":"guide/start.md"}'),t={name:"guide/start.md"};function o(d,l,c,n,h,s){return e(),i("div",null,[...l[0]||(l[0]=[r('<h1 id="начало-игры-на-antagon-smp" tabindex="-1">Начало игры на Antagon SMP <a class="header-anchor" href="#начало-игры-на-antagon-smp" aria-label="Permalink to “Начало игры на Antagon SMP”">​</a></h1><div class="tip custom-block"><p class="custom-block-title">Важно для новичков</p><p>Перед началом игры убедитесь, что у вас:</p><ol><li>Установлен лицензионный Minecraft версии 1.21 или выше</li><li>Прочитаны <a href="/rules.html">правила сервера</a></li><li>Есть Discord для общения с другими игроками</li></ol></div><h2 id="подключение-к-серверу" tabindex="-1">Подключение к серверу <a class="header-anchor" href="#подключение-к-серверу" aria-label="Permalink to “Подключение к серверу”">​</a></h2><ol><li>Запустите Minecraft 1.21+</li><li>Перейдите в раздел &quot;Multiplayer&quot;</li><li>Нажмите &quot;Add Server&quot;</li><li>Введите адрес: <code>play.antagon.ru</code></li><li>Присоединяйтесь к серверу</li></ol><h2 id="первые-шаги" tabindex="-1">Первые шаги <a class="header-anchor" href="#первые-шаги" aria-label="Permalink to “Первые шаги”">​</a></h2><h3 id="_1-безопасность" tabindex="-1">1. Безопасность <a class="header-anchor" href="#_1-безопасность" aria-label="Permalink to “1. Безопасность”">​</a></h3><ul><li>Используйте команду <code>/register &lt;пароль&gt; &lt;пароль&gt;</code> для регистрации</li><li>Если уже регистрировались: <code>/login &lt;пароль&gt;</code></li><li>Запомните или запишите пароль в надёжном месте</li></ul><h3 id="_2-основные-команды" tabindex="-1">2. Основные команды <a class="header-anchor" href="#_2-основные-команды" aria-label="Permalink to “2. Основные команды”">​</a></h3><ul><li><code>/spawn</code> — вернуться на спавн (работает только там)</li><li><code>/rtp</code> — случайная телепортация (доступно раз в час)</li><li><code>/msg &lt;ник&gt; &lt;сообщение&gt;</code> — личное сообщение</li><li><code>/help</code> — список всех доступных команд</li></ul><h3 id="_3-защита-территории" tabindex="-1">3. Защита территории <a class="header-anchor" href="#_3-защита-территории" aria-label="Permalink to “3. Защита территории”">​</a></h3><ul><li><code>/protect</code> — базовые команды защиты</li><li><code>/protect claim</code> — защитить территорию (нужны алмазы)</li><li><code>/protect trust &lt;ник&gt;</code> — доверить территорию игроку</li><li><code>/protect info</code> — информация о привате</li></ul><h2 id="особенности-сервера" tabindex="-1">Особенности сервера <a class="header-anchor" href="#особенности-сервера" aria-label="Permalink to “Особенности сервера”">​</a></h2><h3 id="кастомные-предметы" tabindex="-1">Кастомные предметы <a class="header-anchor" href="#кастомные-предметы" aria-label="Permalink to “Кастомные предметы”">​</a></h3><p>На сервере есть уникальные предметы с особыми свойствами:</p><ul><li>Улучшенные инструменты</li><li>Специальное оружие</li><li>Редкие артефакты</li></ul><p>Рецепты можно узнать у других игроков или найти в игровых структурах.</p><h3 id="особые-мобы" tabindex="-1">Особые мобы <a class="header-anchor" href="#особые-мобы" aria-label="Permalink to “Особые мобы”">​</a></h3><ul><li>В мире появляются необычные существа</li><li>Боссы имеют уникальные механики</li><li>За победу над боссами даются ценные награды</li></ul><h3 id="экономика" tabindex="-1">Экономика <a class="header-anchor" href="#экономика" aria-label="Permalink to “Экономика”">​</a></h3><ul><li>Торгуйте с игроками напрямую или через торговые точки</li><li>Создавайте магазины командой <code>/shop create</code></li><li>Участвуйте в аукционах и торговых событиях</li></ul><h2 id="советы-новичкам" tabindex="-1">Советы новичкам <a class="header-anchor" href="#советы-новичкам" aria-label="Permalink to “Советы новичкам”">​</a></h2><ol><li><p><strong>Первая ночь</strong></p><ul><li>Быстро соберите дерево и сделайте базовые инструменты</li><li>Найдите или постройте укрытие</li><li>Подготовьте факелы и кровать</li></ul></li><li><p><strong>Первая неделя</strong></p><ul><li>Обустройте базовый дом</li><li>Начните ферму еды</li><li>Найдите железо и алмазы</li><li>Защитите территорию</li></ul></li><li><p><strong>Развитие</strong></p><ul><li>Присоединитесь к городу или основайте свой</li><li>Торгуйте с другими игроками</li><li>Участвуйте в событиях сервера</li><li>Исследуйте мир и ищите особых мобов</li></ul></li></ol><h2 id="полезные-ссылки" tabindex="-1">Полезные ссылки <a class="header-anchor" href="#полезные-ссылки" aria-label="Permalink to “Полезные ссылки”">​</a></h2><ul><li><a href="https://discord.gg/antagon" target="_blank" rel="noreferrer">Discord сервер</a> — общение, новости, события</li><li><a href="/rules.html">Правила сервера</a> — важно знать и соблюдать</li><li><a href="https://map.antagon.ru" target="_blank" rel="noreferrer">Карта сервера</a> — динамическая карта мира</li></ul><h2 id="часто-задаваемые-вопросы" tabindex="-1">Часто задаваемые вопросы <a class="header-anchor" href="#часто-задаваемые-вопросы" aria-label="Permalink to “Часто задаваемые вопросы”">​</a></h2><h3 id="как-защитить-свои-дом" tabindex="-1">Как защитить свой дом? <a class="header-anchor" href="#как-защитить-свои-дом" aria-label="Permalink to “Как защитить свой дом?”">​</a></h3><p>Используйте систему приватов через команду <code>/protect claim</code>. Для защиты территории нужны алмазы — чем больше территория, тем больше алмазов потребуется.</p><h3 id="что-делать-если-меня-убили" tabindex="-1">Что делать, если меня убили? <a class="header-anchor" href="#что-делать-если-меня-убили" aria-label="Permalink to “Что делать, если меня убили?”">​</a></h3><p>После смерти вы возродитесь в точке спавна или у своей кровати. У вас есть 5 минут, чтобы вернуться к месту смерти и забрать вещи.</p><h3 id="как-заработать-первые-ресурсы" tabindex="-1">Как заработать первые ресурсы? <a class="header-anchor" href="#как-заработать-первые-ресурсы" aria-label="Permalink to “Как заработать первые ресурсы?”">​</a></h3><p>Начните с добычи дерева и камня, затем перейдите к железу. Продавайте лишние ресурсы другим игрокам или через магазины. Участвуйте в событиях сервера для получения наград.</p><h3 id="как-наити-город-или-команду" tabindex="-1">Как найти город или команду? <a class="header-anchor" href="#как-наити-город-или-команду" aria-label="Permalink to “Как найти город или команду?”">​</a></h3><p>Спросите в чате или на Discord сервере. Многие города ищут новых жителей и будут рады вам помочь освоиться.</p><div class="warning custom-block"><p class="custom-block-title">Важно помнить</p><ul><li>Гриферство и читы запрещены</li><li>Уважайте других игроков</li><li>Если есть вопросы — спрашивайте в чате или Discord</li></ul></div><hr><p>Если у вас остались вопросы, всегда можно:</p><ol><li>Спросить в игровом чате</li><li>Обратиться к модераторам</li><li>Задать вопрос в Discord</li><li>Проверить каналы объявлений</li></ol>',37)])])}const b=a(t,[["render",o]]);export{u as __pageData,b as default};
